				if(degreeArraySimplification.indexOf("2")>-1) currentScaleBinaryUnrooted[2]=1;
				if(degreeArraySimplification.indexOf("2#")>-1||degreeArraySimplification.indexOf("3b")>-1) currentScaleBinaryUnrooted[3]=1;
