				if(degreeArraySimplification.indexOf("4")>-1) currentScaleBinaryUnrooted[5]=1;
				if(degreeArraySimplification.indexOf("4#")>-1||degreeArraySimplification.indexOf("5b")>-1) currentScaleBinaryUnrooted[6]=1;
