				if(degreeArraySimplification.indexOf("7")>-1) currentScaleBinaryUnrooted[11]=1;
				
				var index;
				if((index=scales.map(x => x.scaleBinary.join("")).indexOf(currentScaleBinaryUnrooted.join("")))>-1){
					setNewScale(index, newRootNote);
					return;
				}
				
